document.getElementById('messLink').onclick = function(event) {
    event.preventDefault(); // 阻止默认的链接行为

    // 使用 fetch 加载留言板页面的内容
    fetch('/mess')
        .then(response => response.text())
        .then(data => {
            // 将加载的内容插入到当前页面中
            document.body.innerHTML = data;
        })
        .catch(error => {
            console.error('Error fetching content:', error);
        });
};